import React, { useState } from "react";

function Card({id, image, job, name, info}){

    const description = `${info.subString(0,100)}....`;

    const [readmore, setReadMore] = useState(false);

    function readmoreHandler(){
        setReadMore(!readmore);

        description = readmore ? info : description;
    }



    return(
        <div className="card">

            <img src={image} className="image"></img>

            <div className="tour-details">
                <h4 className="job-profile">{job}</h4>
                <h4 className="person-name">{name}</h4>
            </div>

            <div className="description">
                {description}
                <span onClick={readmoreHandler}>{readmore ? `show less` : `read more`}</span>
            </div>

            <button className="ignorebtn" onClick={ignoreBtnHandler}>IGNORE</button>
            <button className="interestbtn" onClick={interestBtnHandler}>INTERESTED</button>



        </div>

    );
}

export default Card;
