import React, { useState } from "react";

function Card({id, image, job, name, info}){

    const description = `${info.subString(0,100)}....`;

    const [readmore, setReadMore] = useState(false);

    description = readmore ? info : setReadMore(description);

    return(
        <div className="card">

            <img src={image} className="image"></img>

            <div className="tour-details">
                <h4 className="job-profile">{job}</h4>
                <h4 className="person-name">{name}</h4>
            </div>

            <div className="information">
                {description}
                <span>readmore ? `showL` : setReadMore(description);</span>
            </div>

            <button>IGNORE</button>
            <button>INTERESTED</button>



        </div>

    );
}

export default Card;
